import "./Expenses.css";
import ExpenseFilter from "./ExpensesFilter"
import Card from '../UI/Card';
import ExpenseItem from "./ExpenseItem";
import { useState } from "react";

function Expenses(props) {
const [selectedYear, updateSelectedYear] = useState('2020')
const [expenseFilter, updateExpenseFilter] = useState('')
 
  const expenseFilterHandler = (expenseFilter) => {
    updateExpenseFilter(expenseFilter)
    console.log(expenseFilter);
  }
  return (
    <div>
      <Card className="expenses">
        <ExpenseFilter selected={selectedYear} onUpdateExpenseFilter={expenseFilterHandler}/>  
        {props.expenses.map((element) => {
          return (
            <ExpenseItem
              key={element.id}
              title={element.title}
              amount={element.amount}
              date={element.date}
            />
          );
        })}
      </Card>
    </div>
  );
}

export default Expenses;
